mapper.select:
SELECT * FROM sys_user INNER JOIN sys_user_role ON sys_user_role.user_id = sys_user.id INNER JOIN sys_role ON sys_user_role.role_id = sys_role.id WHERE sys_user.id = sys_user_role.user_id AND sys_role.id = sys_user_role.role_id AND sys_user.is_del='0' AND sys_role.id=2
AND name like CONCAT('%',#{name},'%')
AND is_lock =#{isLock}
AND create_time BETWEEN #{time1} and #{time2}

SELECT * FROM sys_user INNER JOIN sys_user_role ON sys_user_role.user_id = sys_user.id INNER JOIN sys_role ON sys_user_role.role_id = sys_role.id WHERE sys_user.id = sys_user_role.user_id AND sys_role.id = sys_user_role.role_id AND sys_user.is_del='0' AND sys_role.id=3
AND name like CONCAT('%',#{name},'%')
AND perms =#{perms}
AND create_time BETWEEN #{time1} and #{time2}
